import { StyleSheet, Text, View } from 'react-native'
import React from 'react'
import { Image } from 'react-native'

const  Card = ({item}) => {
  return (
    <View style={styles.mainContainer}
      >
    <Image style={styles.img} source={item.image} resizeMethod='contain' />
    <Text>
      {item.tittle}
    </Text>
   
    </View>
  )
}

const styles = StyleSheet.create({
  // mainContainer:{
  //   padding:'30',
  //   borderRadius:5,
  //   shadowColor:'grey',
  //   shadowOffset:{width:0 , height: 0},
  //   shadowOpacity:'0.5',
  //   shadowRadius:8,
  //   elevation:8,
  // },
  img:{
    width:'100%',
    height:'undefine',
    aspectRatio:1,   // this is most impotent

  }
})
export default  Card
