import { StyleSheet, Text, View, FlatList } from "react-native";
import React, { useEffect, useState } from "react";
import { ActivityIndicator } from "react-native";

const UserData = () => {
  const [userData, setUserData] = useState();
  const [isLoading, setIsLoading] = useState(true);
  const getUserData = async () => {
    try {
      const response = await fetch(
        "https://thapatechnical.github.io/userapi/users.json"
      );
      const Data = await response.json();
      console.log(Data);
      setUserData(Data);
      setIsLoading(false);
    } catch (error) {
  
      console.log("Api Data Error", error);
    }
  };
  useEffect(() => {
    getUserData();
  }, []);

  return (
    <View>
      {isLoading ? (
        <View>
          <ActivityIndicator />
        </View>
      ) : (
        <View>
          <Text>UserData</Text>
          <FlatList value={Data} renderItem={({item}) => {
               <View>
               <View style={Style.imagContainer}>
                 <Image
                   style={Style.imagContainer}
                   source={{ uri: item.image }}
                   resizeMode="cover"
                 />
               </View>
               <View>
                 <View style={Style.bioDataContainer}>
                   <Text style={Style.bioDataContainer}>Bio-Data</Text>
                   <Text style={Style.idNumber}>
                     {item.id < 10 ? ` #0${item.id} ` : ` #0${item.id} `}
                   </Text>
                 </View>
                 <View style={Style.mainContain}>
                   <Text style={Style.myName}>{item.name}</Text>
                   <Text style={Style.myName}> {item.email} </Text>
                   <Text style={Style.myName}> {item.mobile} </Text>
                 </View>
               </View>
             </View>
          }} />
        </View>
      )}
    </View>
  );
};

const Style = StyleSheet.create({
  imagContainer: {
    width: 100,
    height: 100,
  },
});
export default UserData;
