import { StyleSheet, Text, TouchableOpacity, View } from "react-native";
import React from "react";
import { Image } from "react-native";

const Card = ({ item ,navigation }) => {
  return (
    <View style={styles.mainContainer}>
      <View style={styles.Container} >

 
      <Image style={styles.img} source={item.image} />

      <Text style={styles.tittle}>{item.tittle}</Text>

      <Text style={styles.description}>{item.description}</Text>
      {/* <View style={{display:'flex', flexDirection:'row' , marginTop:12 , justifyContent:'space-around', marginBottom:12 , width:'100%',   }} >
        <Text style={styles.course}>{item.Course1}</Text>
        <Text style={styles.course}>{item.Course2}</Text>
        <Text style={styles.course}>{item.Course3}</Text>
        <Text style={styles.price}>{item.price}</Text>
      </View> */}
      <TouchableOpacity 
      style={styles.btnContainer} 
      onPress={()=> navigation.navigate('courseDetails')}
       >
        <Text    style={styles.btnText} >
          Course Detail
        </Text>
      </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  mainContainer: {
    backgroundColor: "blue",
    width: "100%",
    height: "100%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
 
  },
  Container:{
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    marginBottom:20, 
    // borderWidth:2,
    backgroundColor:'#F2F2F2',
    width:'70%',
    borderRadius: 5,
    shadowColor: "grey",
    shadowOpacity: "0.5",
    shadowRadius: 8,
    elevation: 8,
  },
  imgContainer: {
    // backgroundColor: "yellow",
    height: "100%",
    width: "100%",
  },
  img: {
    width: "100%",
    height: "undefine",
    aspectRatio: 1, // this is most impotent
    resizeMode: "cover",
  },
  tittle: {
    fontSize: 20,
    fontWeight: "700",
    color: "#0B020E",
    // backgroundColor: "green",
    // width: "50%",
    height: "30%",
      marginTop:12 ,
      textTransform:'uppercase'
  },
  description: {
    // backgroundColor: "yellow",
    color: "#595957",
    fontFamily: "bold",
    width:"80%",
    marginTop:12 ,
    
  },
  course:{
    fontSize: 20,
    fontWeight: "400",
    color: "#0B020E",
  },
  price:{
    fontSize: 20,
    fontWeight: "400",
    color: "#0B020E",
  },
  btnContainer:{
  backgroundColor:'#5CC1F7',
  borderRadius:5,
  marginTop:12,
  paddingVertical:7,
  paddingHorizontal:10,
  display:'flex',
  justifyContent:'center',
  alignItems:'center',
  },
  btnText:{
  fontSize:20,
  color:'#eee',
  fontFamily:'bold'

  },
});
export default Card;
