import { StyleSheet, Text, TouchableOpacity, View } from "react-native";
import React from "react";
import { Image } from "react-native";

const Card = ({ item ,navigation }) => {
  return (
    <View style={styles.mainContainer}>
      <View style={styles.Container} >

 
      <Image style={styles.img} source={item.image} />

      <Text style={styles.tittle}>{item.tittle}</Text>

      <Text style={styles.description}>{item.description}</Text>
      <View style={{display:'flex', flexDirection:'row' , marginTop:12 , justifyContent:'space-around', marginBottom:12 , width:'70%',   }} >
        <Text style={styles.course}>{item.Course1}</Text>
        <Text style={styles.course}>{item.Course2}</Text>
        <Text style={styles.course}>{item.Course3}</Text>
        <Text style={styles.price}>{item.price}</Text>
      </View>
      <TouchableOpacity 
      style={styles.btnContainer} 
      onPress={()=> navigation.navigate('courseDetails')}
       >
        <Text>
          Course Detail
        </Text>
      </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  mainContainer: {
    // backgroundColor: "blue",
    width: "100%",
    height: "100%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
 
  },
  Container:{
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    marginBottom:20, 
    
    // borderWidth:2,
    backgroundColor:'#F2F2F2',
    width:'40%',
    borderRadius: 5,
    shadowColor: "grey",
    shadowOpacity: "0.5",
    shadowRadius: 8,
    elevation: 8,
  },
  imgContainer: {
    // backgroundColor: "yellow",
    height: "20%",
    width: "50%",
  },
  img: {
    width: "40%",
    height: "undefine",
    aspectRatio: 1, // this is most impotent
    resizeMode: "cover",
  },
  tittle: {
    fontSize: 20,
    fontWeight: "700",
    color: "#0B020E",
    // backgroundColor: "green",
    // width: "50%",
    height: "30%",
      marginTop:12 ,
      textTransform:'uppercase'
  },
  description: {
    // backgroundColor: "yellow",
    color: "#595957",
    fontFamily: "bold",
    width:"80%",
    marginTop:12 ,
    
  },
  course:{
    fontSize: 20,
    fontWeight: "400",
    color: "#0B020E",
  },
  price:{
    fontSize: 20,
    fontWeight: "400",
    color: "#0B020E",
  },
  btnContainer:{

  }
});
export default Card;
