import { StyleSheet, Text, View } from "react-native";
import React from "react";
import { Image } from "react-native";

const Card = ({ item }) => {
  return (
    <View style={styles.mainContainer}>
      <Image style={styles.img} source={item.image} />

      <Text style={styles.tittle}>{item.tittle}</Text>

      <Text style={styles.description}>{item.description}</Text>
      <View>
        <Text style={styles.course}>{item.Course1}</Text>
        <Text style={styles.course}>{item.Course2}</Text>
        <Text style={styles.course}>{item.Course3}</Text>
        <Text style={styles.price}>{item.price}</Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  mainContainer: {
    borderRadius: 5,
    shadowColor: "grey",
    shadowOffset: { width: 0, height: 0 },
    shadowOpacity: "0.5",
    shadowRadius: 8,
    elevation: 8,
    backgroundColor: "blue",
    width: "100%",
    height: "100%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  imgContainer: {
    backgroundColor: "yellow",
    height: "20%",
    width: "20%",
  },
  img: {
    width: "40%",
    height: "undefine",
    aspectRatio: 1, // this is most impotent
    resizeMode: "cover",
  },
  tittle: {
    fontSize: 20,
    fontWeight: "700",
    color: "#0B020E",
    backgroundColor: "green",
    width: "50%",
    height: "30%",
  },
  description: {
    backgroundColor: "yellow",
    color: "#595957",
    fontFamily: "bold",
  },
});
export default Card;
